import 'package:flutter/material.dart';
class ActionIconButton extends StatelessWidget { final IconData icon; final String label; final VoidCallback onTap; final bool isActive; const ActionIconButton({super.key, required this.icon, required this.label, required this.onTap, this.isActive = false}); @override Widget build(BuildContext context) { final c = isActive ? Colors.amber : Colors.white; return InkWell(onTap: onTap, borderRadius: BorderRadius.circular(8), child: Padding(padding: const EdgeInsets.all(8.0), child: Column(mainAxisSize: MainAxisSize.min, children: [Icon(icon, color: c, size: 24), const SizedBox(height: 4), Text(label, style: const TextStyle(color: Colors.white70, fontSize: 10))]))); } }
